#coding: utf-8import timeglobal start_timestart_time = time.time()def upTime(bot, msg):    print("/uptime was calld")        chat_id = msg['chat']['id']            r_t_hours = (time.time() - start_time) // 3600    r_t_minutes = (time.time() - start_time) % 3600 / 60    r_t_seconds = (time.time() - start_time) % 3600 % 60        if r_t_hours >= 1:        bot.sendMessage(chat_id, "{:.0f}h {:.0f}min".format(r_t_hours, r_t_minutes,))        return "{:.0f}h {:.0f}min".format(r_t_hours, r_t_minutes,)    elif r_t_minutes >= 1:        bot.sendMessage(chat_id, "{:.0f}min {:.0f}seconds".format(r_t_minutes, r_t_seconds))        return "{:.0f}min {:.0f}seconds".format(r_t_minutes, r_t_seconds)    else:        bot.sendMessage(chat_id, "{:.2f} seconds".format(r_t_seconds))        return "{:.2f} seconds".format(r_t_seconds)        return