import telepotfrom vertaa import *from telepot.loop import *from archive import *bot = telepot.Bot(joukkueBot)response = bot.getUpdates()#print(bot.getMe())online = 0def handle(msg):    content_type, chat_type, chat_id = telepot.glance(msg)    print(content_type, chat_type, chat_id)    if online != 0:        if content_type == 'text':            value = comparing(msg['text'])            if value.is_tuli() == True:                bot.sendMessage(chat_id, 'spruit')                bot.sendSticker(chat_id, spruit)            if value.is_tissit() == True:                bot.sendPhoto(chat_id, boobs)            if msg['text'] == "/moi":                bot.sendMessage(chat_id, "moi")        if content_type == 'voice':            bot.sendMessage(chat_id, 'Lul i have no ears')        if content_type == 'new_chat_member':            bot.sendMessage(chat_id, 'Tervetuloa norsunluutorniin, voittajien valinta')MessageLoop(bot, handle).run_as_thread()print ('The bot is on')# Keep the program running.while 1:    if online == 0:        time.sleep(60)        online = 1    time.sleep(10)    #bot.sendMessage(-1001059712421L, 'Moi, en ees tullu pakottaa')    #response = bot.getUpdates()    #pprint(response)#def handle(msg):#pprint(msg)#MessageLoop(bot, handle).run_forever()##bot.sendMessage(42231217, 'The bot is creepy (:!')